import re

def nome_do_topico_da_branch(branch_name):
    # Use expressões regulares para extrair o nome do tópico
    match = re.match(r'^.*\/(.*)$', branch_name)
    if match:
        return match.group(1)
    else:
        return None

# Exemplo de uso
branch_name = "nome-do-desenvolvedor/tópico-recursos-web"
nome_do_topico = nome_do_topico_da_branch(branch_name)

if nome_do_topico:
    print(f"Nome do Tópico: {nome_do_topico}")
    # Aqui você pode realizar a lógica para substituir o conteúdo do mapa mental
else:
    print("Padrão de nomenclatura não reconhecido.")
